var TasksController;

TasksController = {
  init: function() {
    this.tasks = [];
    this.taskTemplate = $('#task-template').html();
    return Mustache.parse(this.taskTemplate);
  },
  add: function(title) {
    if ($.trim(title)) {
      this.tasks.push(title);
      return $('ul#tasks').append(this.taskTag(title));
    }
  },
  taskTag: function(title) {
    return Mustache.render(this.taskTemplate, {
      title: title
    });
  }
};

$(function() {
  TasksController.init();
  return $('#new-task').submit(function(ev) {
    ev.preventDefault();
    TasksController.add($(this).find('input').val());
    this.reset();
    return false;
  });
});
